	// ------------------------------------------------------------------
	// formatDate (date_object, format, convertDateToUTC)
	// Returns a date in the output format specified.
	// The format string uses the same abbreviations as in getDateFromFormat()
	// Field        | Full Form          | Short Form
	// -------------+--------------------+-----------------------
	// Year         | yyyy (4 digits)    | yy (2 digits), y (2 or 4 digits)
	// Month        | MMM (name or abbr.)| MM (2 digits), M (1 or 2 digits)
	//              | NNN (abbr.)        |
	//				| FFF (french abbr.) |
	// Day of Month | dd (2 digits)      | d (1 or 2 digits)
	// Day of Week  | EE (name)          | E (abbr)
	// Hour (1-12)  | hh (2 digits)      | h (1 or 2 digits)
	// Hour (0-23)  | HH (2 digits)      | H (1 or 2 digits)
	// Hour (0-11)  | KK (2 digits)      | K (1 or 2 digits)
	// Hour (1-24)  | kk (2 digits)      | k (1 or 2 digits)
	// Minute       | mm (2 digits)      | m (1 or 2 digits)
	// Second       | ss (2 digits)      | s (1 or 2 digits)
	// AM/PM        | a                  |
	// UTC +08:00   | Z                  |
	//
	// NOTE THE DIFFERENCE BETWEEN MM and mm! Month=MM, not mm!
	// Examples:
	//  "MMM d, y" matches: January 01, 2000
	//                      Dec 1, 1900
	//                      Nov 20, 00
	//  "M/d/yy"   matches: 01/20/00
	//                      9/2/00
	//  "MMM dd, yyyy hh:mm:ssa" matches: "January 01, 2000 12:30:45AM"
	//
	// NOTE if date_object is already in UTC time, set parameter convertDateToUTC to false
	// ------------------------------------------------------------------
	MONTH_NAMES: ['January','February','March','April','May','June','July','August','September','October','November','December','Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
	FRENCH_MONTH_NAMES: ['janv.','fvr.','mars','avril','mai','juin','juil.','aot','sept.','oct.','nov.','dc.'],
	DAY_NAMES: ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday','Sun','Mon','Tue','Wed','Thu','Fri','Sat'],
	LZ: function(x) {return(x<0||x>9?"":"0")+x},

	formatDate: function(date, format, convertDateToUTC) {
		format=format+"";
		var result="";
		var i_format=0;
		var c="";
		var token="";
		var y="", M=0, d=0, E=0, H=0, m=0, s=0, Z=0, ZS="", ZH="", ZM="";
		Z=date.getTimezoneOffset()/60;  // 480 / 60 = UTC -08:00
		if( Z<0 ){
			ZS="+";
		}
		else {
			ZS="-";
		}
		Z=Math.abs(Z);
		if( Z<10 ) {
			ZH="0"+Math.floor(Z);
		}
		else {
			ZH=Math.floor(Z);
		}
		ZM=(Z-Math.floor(Z))*60;
		if( ZM<10 ) {
			ZM="0"+ZM;
		}
		if(convertDateToUTC != null && convertDateToUTC) {
			y=date.getUTCFullYear()+"";
			M=date.getUTCMonth()+1;
			d=date.getUTCDate();
			E=date.getUTCDay();
			H=date.getUTCHours();
			m=date.getUTCMinutes();
			s=date.getUTCSeconds();
		}
		else {
			y=date.getFullYear()+"";
			M=date.getMonth()+1;
			d=date.getDate();
			E=date.getDay();
			H=date.getHours();
			m=date.getMinutes();
			s=date.getSeconds();
		}
		var yyyy,yy,MMM,MM,dd,hh,h,mm,ss,ampm,HH,H,KK,K,kk,k;
		// Convert real date parts into formatted versions
		var value=new Object();
		if (y.length < 4) {y=""+(y-0+1900);}
		value["y"]=""+y;
		value["yyyy"]=y;
		value["yy"]=y.substring(2,4);
		value["M"]=M;
		value["MM"]=this.LZ(M);
		value["MMM"]=this.MONTH_NAMES[M-1];
		value["NNN"]=this.MONTH_NAMES[M+11];
		value["FFF"]=this.FRENCH_MONTH_NAMES[M-1];
		value["d"]=d;
		value["dd"]=this.LZ(d);
		value["E"]=this.DAY_NAMES[E+7];
		value["EE"]=this.DAY_NAMES[E];
		value["H"]=H;
		value["HH"]=this.LZ(H);
		if (H==0){value["h"]=12;}
		else if (H>12){value["h"]=H-12;}
		else {value["h"]=H;}
		value["hh"]=this.LZ(value["h"]);
		if (H>11){value["K"]=H-12;} else {value["K"]=H;}
		value["k"]=H;
		value["KK"]=this.LZ(value["K"]);
		value["kk"]=this.LZ(value["k"]);
		if (H > 11) { value["a"]="PM"; }
		else { value["a"]="AM"; }
		value["m"]=m;
		value["mm"]=this.LZ(m);
		value["s"]=s;
		value["ss"]=this.LZ(s);
		value["Z"]="UTC"+ZS+ZH+":"+ZM;
		var inQuotes = false;
		while (i_format < format.length) {
			c=format.charAt(i_format);
			token="";
			while ((format.charAt(i_format)==c) && (i_format < format.length)) {
				token += format.charAt(i_format++);
			}
			if ( token == "'" ) {
				inQuotes = !inQuotes;
			}
			else if (!inQuotes && value[token] != null) {
				result=result + value[token];
			}
			else {
				result=result + token;
			}
		}
		return result;
	},

